import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import Navbar from './Navbar';
import { Deck } from '../types/Deck';
import { SearchResult } from '../types/SearchResult';

// Removing the conflicting inline styles entirely - using only the CSS file styles

interface User {
  id: string;
  email: string;
}

interface Card {
  id: string;
  answer: string;
  video_url: string;
  deck_id: string;
}

const Home: React.FC = () => {
  const [decks, setDecks] = useState<Deck[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<{ cards: SearchResult[], decks: Deck[] }>({
    cards: [],
    decks: []
  });
  const [showSearchResults, setShowSearchResults] = useState(false);
  const [userId, setUserId] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        console.log('Starting data fetch...');
        // First, get the demo user
        const usersResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/users`);

        console.log('Users API response:', usersResponse.data);

        if (!usersResponse.data || usersResponse.data.length === 0) {
          throw new Error('No users found');
        }

        const demoUser = usersResponse.data.find((user: User) => user.email === 'demo@example.com');
        console.log('Found demo user:', demoUser);

        if (!demoUser) {
          throw new Error('Demo user not found');
        }

        // Then fetch decks for that user
        console.log('Fetching decks for user:', demoUser.id);
        const decksResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/decks/${demoUser.id}`);

        console.log('Decks API response:', decksResponse.data);

        if (!decksResponse.data) {
          throw new Error('No decks data received');
        }

        const fetchedDecks = decksResponse.data;
        setDecks(fetchedDecks);
        console.log(`Fetched ${fetchedDecks.length} decks`);

        setLoading(false);
      } catch (err: any) {
        console.error('Error fetching data:', err);
        setError(err.message || 'Failed to load decks');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    try {
      setLoading(true);
      const response = await fetch(`/api/search?term=${encodeURIComponent(searchTerm)}`);

      if (!response.ok) {
        throw new Error("Failed to search");
      }

      const data = await response.json();
      setSearchResults(data);
      setShowSearchResults(true);
      setLoading(false);
    } catch (err) {
      setError("Failed to search. Please try again.");
      setLoading(false);
    }
  };

  const handleCardResultClick = (deckId: string) => {
    navigate(`/deck/${deckId}`);
  };

  const handleDeckResultClick = (deckId: string) => {
    navigate(`/deck/${deckId}`);
  };

  if (loading) {
    return (
      <div className="home-container loading">
        <div className="loading-spinner"></div>
        <p>Loading decks...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="home-container error">
        <div className="error-message">
          <h2>Error</h2>
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar userId={userId} />

      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-center mb-8">ASL Study Tool</h1>

        <div className="max-w-xl mx-auto">
          <form onSubmit={handleSearch} className="mb-8">
            <div className="flex items-center">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search for signs..."
                className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                Search
              </button>
            </div>
          </form>

          {showSearchResults && (
            <div className="mb-8">
              <h2 className="text-xl font-semibold mb-4">Search Results</h2>

              {searchResults.cards.length > 0 && (
                <div className="mb-6">
                  <h3 className="text-lg font-medium mb-2">Signs</h3>
                  <ul className="space-y-2">
                    {searchResults.cards.map((result) => (
                      <li
                        key={result.id}
                        onClick={() => handleCardResultClick(result.deck_id)}
                        className="p-3 bg-white rounded shadow cursor-pointer hover:bg-gray-50"
                      >
                        <p className="font-medium">{result.answer}</p>
                        <p className="text-sm text-gray-600">Deck: {result.deck.title}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {searchResults.decks.length > 0 && (
                <div>
                  <h3 className="text-lg font-medium mb-2">Decks</h3>
                  <ul className="space-y-2">
                    {searchResults.decks.map((deck) => (
                      <li
                        key={deck.id}
                        onClick={() => handleDeckResultClick(deck.id)}
                        className="p-3 bg-white rounded shadow cursor-pointer hover:bg-gray-50"
                      >
                        <p className="font-medium">{deck.title}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {searchResults.cards.length === 0 && searchResults.decks.length === 0 && (
                <p className="text-center text-gray-600">No results found</p>
              )}

              <button
                onClick={() => {
                  setShowSearchResults(false);
                  setSearchTerm("");
                }}
                className="mt-4 text-blue-500 hover:text-blue-700"
              >
                Clear Search
              </button>
            </div>
          )}

          {!showSearchResults && (
            <>
              <h2 className="text-xl font-semibold mb-4">Your Decks</h2>

              {loading ? (
                <p className="text-center text-gray-600">Loading...</p>
              ) : error ? (
                <p className="text-center text-red-500">{error}</p>
              ) : decks.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {decks.map((deck) => (
                    <div
                      key={deck.id}
                      onClick={() => navigate(`/deck/${deck.id}`)}
                      className="p-4 bg-white rounded shadow cursor-pointer hover:bg-gray-50"
                    >
                      <h3 className="text-lg font-medium">{deck.title}</h3>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-center text-gray-600">No decks found. Create your first deck!</p>
              )}

              <div className="mt-6 text-center">
                <button
                  onClick={() => navigate("/create-deck")}
                  className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500"
                >
                  Create New Deck
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
