import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import ReactPlayer from 'react-player';
import './TestMode.css';
import { LOCAL_STORAGE_STARRED_KEY } from './constants';

interface Card {
  id: string;
  video_url: string;
  answer: string;
  deck_id: string;
}

interface TestModeProps {
  deckId: string;
}

const TestMode: React.FC<TestModeProps> = ({ deckId }) => {
  const [cards, setCards] = useState<Card[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [testComplete, setTestComplete] = useState(false);
  const [results, setResults] = useState<{ correct: Card[], incorrect: Card[], userAnswers: string[] }>({
    correct: [],
    incorrect: [],
    userAnswers: []
  });
  const [videoSource, setVideoSource] = useState<string>('');
  const [starredCardIds, setStarredCardIds] = useState<string[]>([]);
  const [testMode, setTestMode] = useState<'all' | 'starred'>('all');
  const [loadingStarred, setLoadingStarred] = useState(true);

  const navigate = useNavigate();
  const location = useLocation();

  // Fetch starred cards
  useEffect(() => {
    const fetchStarredCards = async () => {
      try {
        // First try to get from server
        const demoUserId = "demo-user-id"; // Hardcoded for demo

        // Add debugging to find issue
        console.log("TestMode: Fetching starred cards...");

        // Check localStorage first since we know it works
        const localStarred = localStorage.getItem(LOCAL_STORAGE_STARRED_KEY);
        if (localStarred) {
          try {
            const parsedStarred = JSON.parse(localStarred);
            console.log('TestMode: Using localStorage starred cards:', parsedStarred);
            setStarredCardIds(parsedStarred);
            setLoadingStarred(false);

            // Check if we should only test starred cards
            if (location.state && (location.state as any).starredOnly) {
              console.log("TestMode: Setting test mode to starred");
              setTestMode('starred');
            }
            return; // Exit early with localStorage values
          } catch (parseErr) {
            console.error('TestMode: Error parsing starred cards from localStorage:', parseErr);
          }
        }

        // Try server as fallback
        try {
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/users/${demoUserId}/starred-card-ids`);

          if (response.data && response.data.cardIds && response.data.cardIds.length > 0) {
            console.log("TestMode: Loaded starred cards from server:", response.data.cardIds.length);
            setStarredCardIds(response.data.cardIds || []);
          } else {
            console.log('TestMode: No starred cards found on server or localStorage');
            setStarredCardIds([]);
          }
        } catch (serverErr) {
          console.error("TestMode: Error fetching starred cards from server:", serverErr);
          setStarredCardIds([]);
        }
      } catch (err) {
        console.error("TestMode: Error in starred card fetching logic:", err);
        setStarredCardIds([]);
      } finally {
        setLoadingStarred(false);
      }
    };

    fetchStarredCards();

    // We'll set this in the fetchStarredCards function to ensure it happens
    // after the cards are loaded
    // if (location.state && (location.state as any).starredOnly) {
    //   setTestMode('starred');
    // }
  }, [location.state]);

  useEffect(() => {
    const fetchCards = async () => {
      try {
        // Special case for testing all starred cards across all decks
        if (deckId === 'all-decks' && testMode === 'starred') {
          console.log("Testing all starred cards across all decks");

          // Get all decks
          const usersResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/users`);
          const demoUser = usersResponse.data.find((user: any) => user.email === 'demo@example.com');

          if (demoUser) {
            const decksResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/decks/${demoUser.id}`);
            const allDecks = decksResponse.data;
            console.log(`Found ${allDecks.length} decks to search for starred cards`);

            // Collect cards from all decks
            let allCards: Card[] = [];
            for (const deck of allDecks) {
              try {
                const deckCardsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/cards/${deck.id}`);
                allCards = [...allCards, ...deckCardsResponse.data];
              } catch (deckErr) {
                console.error(`Error fetching cards for deck ${deck.id}:`, deckErr);
              }
            }

            console.log(`Found total of ${allCards.length} cards across all decks`);

            // Filter to starred cards only
            if (starredCardIds.length === 0) {
              console.log("No starred cards found, showing empty set");
              allCards = [];
            } else {
              const filteredCards = allCards.filter(card => starredCardIds.includes(card.id));
              console.log(`Filtered to ${filteredCards.length} starred cards across all decks`);
              allCards = filteredCards;
            }

            // Shuffle and set cards
            const shuffledCards = allCards.sort(() => Math.random() - 0.5);
            console.log(`Final count for testing: ${shuffledCards.length} cards`);
            setCards(shuffledCards);
          }

          setLoading(false);
          return; // Exit early
        }

        // Standard single deck test
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/cards/${deckId}`);
        let fetchedCards = [...response.data];

        console.log("Total cards in deck:", fetchedCards.length);
        console.log("Current test mode:", testMode);
        console.log("Starred card IDs:", starredCardIds);

        // If we're in starred-only mode, filter the cards
        if (testMode === 'starred') {
          if (starredCardIds.length === 0) {
            console.log("No starred cards found, showing empty set");
            fetchedCards = [];
          } else {
            const filteredCards = fetchedCards.filter(card => starredCardIds.includes(card.id));
            console.log("Filtered to starred cards:", filteredCards.length);
            fetchedCards = filteredCards;
          }
        }

        // Shuffle the cards for the test
        const shuffledCards = fetchedCards.sort(() => Math.random() - 0.5);
        console.log("Final cards for testing:", shuffledCards.length);
        setCards(shuffledCards);
        setLoading(false);
      } catch (err) {
        console.error('Failed to load cards:', err);
        setError('Failed to load cards');
        setLoading(false);
      }
    };

    // Make sure to wait for starredCardIds to be loaded
    if (!loadingStarred) {
      fetchCards();
    }
  }, [deckId, testMode, starredCardIds, loadingStarred]);

  useEffect(() => {
    if (cards.length > 0 && currentCardIndex < cards.length) {
      const currentCard = cards[currentCardIndex];
      // Transform Google Drive URL if needed (similar to Flashcard component)
      if (currentCard.video_url.includes('drive.google.com')) {
        const transformedUrl = transformGoogleDriveUrl(currentCard.video_url);
        setVideoSource(transformedUrl);
      } else {
        setVideoSource(currentCard.video_url);
      }
    }
  }, [cards, currentCardIndex]);

  // Add keyboard event listener for Enter key to navigate to next card
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Enter' && submitted && !testComplete) {
        handleNextCard();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [submitted, testComplete, currentCardIndex, cards.length]);

  const transformGoogleDriveUrl = (url: string): string => {
    // Check if it's a Google Drive URL
    if (url.includes('drive.google.com')) {
      // Extract file ID from URL
      let fileId = '';

      // Handle different Google Drive URL formats
      if (url.includes('drive.google.com/file/d/')) {
        // Format: https://drive.google.com/file/d/{fileId}/view
        const match = url.match(/\/file\/d\/([^\/]+)/);
        if (match && match[1]) {
          fileId = match[1];
        }
      } else if (url.includes('drive.google.com/open?id=')) {
        // Format: https://drive.google.com/open?id={fileId}
        const match = url.match(/open\?id=([^&]+)/);
        if (match && match[1]) {
          fileId = match[1];
        }
      } else if (url.includes('id=')) {
        // Format: https://drive.google.com/uc?id={fileId}
        const match = url.match(/id=([^&]+)/);
        if (match && match[1]) {
          fileId = match[1];
        }
      }

      if (fileId) {
        return `https://drive.google.com/file/d/${fileId}/preview`;
      }
    }

    return url;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!submitted && cards.length > 0) {
      const currentCard = cards[currentCardIndex];
      const isCorrect = userAnswer.trim().toLowerCase() === currentCard.answer.trim().toLowerCase();

      // Update results
      if (isCorrect) {
        setResults(prevResults => ({
          ...prevResults,
          correct: [...prevResults.correct, currentCard],
          userAnswers: [...prevResults.userAnswers, userAnswer]
        }));
      } else {
        setResults(prevResults => ({
          ...prevResults,
          incorrect: [...prevResults.incorrect, currentCard],
          userAnswers: [...prevResults.userAnswers, userAnswer]
        }));

        // Auto-star incorrect answers
        if (!starredCardIds.includes(currentCard.id)) {
          try {
            // In a real app, you'd get the actual user ID from auth
            const demoUserId = "demo-user-id"; // Hardcoded for demo

            // Update local state first
            const newStarredIds = [...starredCardIds, currentCard.id];
            setStarredCardIds(newStarredIds);

            // Save to localStorage
            localStorage.setItem(LOCAL_STORAGE_STARRED_KEY, JSON.stringify(newStarredIds));

            // Try to star on server (but don't fail if it doesn't work)
            await axios.post(`${process.env.REACT_APP_API_URL}/api/cards/${currentCard.id}/star`, { 
              userId: demoUserId 
            });
          } catch (err) {
            console.error("Error starring incorrect card on server:", err);
            // Already updated localStorage, so we don't need to do anything else
          }
        }
      }

      setSubmitted(true);
    }
  };

  const handleNextCard = () => {
    if (currentCardIndex < cards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
      setUserAnswer('');
      setSubmitted(false);
    } else {
      setTestComplete(true);
    }
  };

  const handleBackToDeck = () => {
    // If we're testing all decks, go back to home
    if (deckId === 'all-decks') {
      navigate('/');
    } else {
      // Otherwise go back to the specific deck
      navigate(`/deck/${deckId}`);
    }
  };

  const handleStartOver = () => {
    setCurrentCardIndex(0);
    setUserAnswer('');
    setSubmitted(false);
    setTestComplete(false);
    setResults({
      correct: [],
      incorrect: [],
      userAnswers: []
    });
  };

  if (loading) {
    return (
      <div className="test-container loading">
        <div className="loading-spinner"></div>
        <p>Loading test...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="test-container error">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={handleBackToDeck} className="back-to-deck-btn">
          Back to Deck
        </button>
      </div>
    );
  }

  if (cards.length === 0) {
    return (
      <div className="test-container error">
        <h2>No Cards Available</h2>
        {testMode === 'starred' ? (
          <p>You don't have any starred cards in this deck yet. Star some cards first or try the full test.</p>
        ) : (
          <p>This deck doesn't have any cards to test with.</p>
        )}
        <button onClick={handleBackToDeck} className="back-to-deck-btn">
          Back to Deck
        </button>
      </div>
    );
  }

  if (testComplete) {
    const totalCards = results.correct.length + results.incorrect.length;
    const score = Math.round((results.correct.length / totalCards) * 100);

    return (
      <div className="test-container results">
        <h2>Test Complete!</h2>
        <div className="test-summary">
          <div className="score">Score: {score}%</div>
          <div className="stats">
            <div className="correct-count">{results.correct.length} correct</div>
            <div className="incorrect-count">{results.incorrect.length} incorrect</div>
          </div>
        </div>

        {results.incorrect.length > 0 && (
          <div className="incorrect-cards">
            <h3>Incorrect Answers (Automatically Starred)</h3>
            <ul className="results-list">
              {results.incorrect.map((card, index) => (
                <li key={card.id} className="result-item">
                  <div className="result-answer">
                    <strong>Correct answer:</strong> {card.answer}
                  </div>
                  <div className="result-user-answer">
                    <strong>Your answer:</strong> {results.userAnswers[results.correct.length + index]}
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}

        <div className="test-actions">
          <button onClick={handleStartOver} className="start-over-btn">
            Start Over
          </button>
          <button onClick={handleBackToDeck} className="back-to-deck-btn">
            Back to Deck
          </button>
        </div>
      </div>
    );
  }

  const currentCard = cards[currentCardIndex];
  const isCorrect = submitted && userAnswer.trim().toLowerCase() === currentCard.answer.trim().toLowerCase();

  return (
    <div className="test-container">
      <div className="test-header">
        <button onClick={handleBackToDeck} className="back-btn">
          &larr; Back to Deck
        </button>
        <h1>ASL Test</h1>
        <div className="progress">
          {currentCardIndex + 1} of {cards.length}
          {testMode === 'starred' && ' (Starred Cards Only)'}
        </div>
      </div>

      <div className="test-content">
        <div className="video-container">
          {videoSource && (
            videoSource.includes('/preview') ? (
              <iframe 
                src={videoSource}
                width="100%" 
                height="100%" 
                allow="autoplay" 
                allowFullScreen
                style={{ border: 'none' }}
              ></iframe>
            ) : (
              <ReactPlayer
                url={videoSource}
                width="100%"
                height="100%"
                controls
                playing={!submitted}
                config={{
                  file: {
                    attributes: {
                      controlsList: 'nodownload',
                      disablePictureInPicture: true
                    },
                    forceVideo: true
                  }
                }}
              />
            )
          )}
        </div>

        {!submitted ? (
          <form onSubmit={handleSubmit} className="answer-form">
            <div className="input-group">
              <label htmlFor="answer">What is the sign?</label>
              <input
                id="answer"
                type="text"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                placeholder="Type your answer here"
                autoComplete="off"
                autoFocus
                required
              />
            </div>
            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        ) : (
          <div className={`feedback ${isCorrect ? 'correct' : 'incorrect'}`}>
            <h2>
              {isCorrect ? 'Correct!' : 'Incorrect'}
            </h2>
            <p>
              The correct answer is: <strong>{currentCard.answer}</strong>
              {!isCorrect && <span className="auto-starred"> (Auto-starred for review)</span>}
            </p>
            <button onClick={handleNextCard} className="next-btn">
              {currentCardIndex < cards.length - 1 ? 'Next Sign' : 'See Results'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default TestMode;
